import React from 'react';
import '../index.css';
class Board extends React.Component{

  showAlert = (event) => {
    console.log("Event is", event.target.key);
    console.log("you have clicked block");
  }

  processClick(position){
    //TODO handle position click.
  }

  render(){

    var color=0;
    var squares=[];
    for(let i=0;i<64;i++)
    {
      if(i%16===0){
        squares.push(<div className="square-white" key={i} onClick={() => {
          console.log(i);
          this.processClick(i);
        }}></div>);
        color=0;
      }
      else if(i%8===0){
        squares.push(<div className="square-black" key={i} onClick={this.showAlert}
        ></div>);
        color=1;
      }
      else if(color===1){
        squares.push(<div className="square-white" key={i} onClick={this.showAlert}></div>);
        color=0;
      }
      else{
        squares.push(<div className="square-black" key={i} onClick={this.showAlert}></div>);
        color=1;
      }
    }
    return (

      <div className="board">{squares}</div>

    );
  }
}

export default Board;
