  retPosition(y,x){
    let yCord = y;
    let xCord = x % 8;

    let xAxis = ['a','b','c','d','e','f','g','h'];

    let retVal = xAxis[xCord]+yCord;
    //console.log(retVal);
    return retVal;

  }
  isEven(num){
    return (num % 2 === 0);
  }
  map(str){
    const c=[];
    for(let i=0;i<str.length;i++){
      var char=str[i];
      c[i] = ini(char);
      //this.setState({pieceSrc: c});

    }
    return(c);
  }
/*  getPiece(pos){

