*/

  eventReset=(e)=>{
  //  this.map(this.chess.fen());
    e.preventDefault();
    var a = this.map(this.chess.fen());
    console.log(a);
    this.state.pieceSrc= this.a.map(item => (this.setState({ pieceSrc : item })));
    //this.setState({ pieceSrc : this.a });           //this.state.pieceSrc.push(this.map(this.chess.fen()))});
    console.log(this.map(this.chess.fen()));
    console.log(this.state.pieceSrc);
  }
  mapArrayToBoard(i,j,ar){
    var p = this.retPosition(i,j);
    switch(p)
    {
      case 'a1':
        return(ar[0]);
        break;
      default:
          return(ar[18]);
    }
  }

  render(){
    var completeBoard =[];
    for(var i=8;i>=1;i--){
      var rowSquare =[];
      for(var j=0;j<8;j++){
        const squareColor = (this.isEven(i) && this.isEven(j)) || (!this.isEven(i) && !this.isEven(j))? "square-white" : "square-black";
        rowSquare.push(<Square
                          color={squareColor}
                          position={this.retPosition(i,j)}
                          key={this.retPosition(i,j)}
                          pieceSrc={this.state.pieceSrc}
                      />);
      }
      completeBoard.push(rowSquare);
    }

  /*  var color=0;
    var squares=[];
    for(let i=0;i<64;i++)
    {
