        squares.push(<Square color={"square-black"} position={this.retPosition(i)} piece={""} />);
        color = 1;
      }
      else if(color===1){
        squares.push(<Square color={"square-white"} position={this.retPosition(i)} piece={""} />);
        color=0;
      }
      else{
        squares.push(<Square color={"square-black"} position={this.retPosition(i)} currentPiece={""} />);
        color=1;
      }
    }  */
    return (
      <div>
      <button
        onClick={this.eventReset}
      >Reset</button>
      <div className="board">{completeBoard}</div>

      </div>
    );
  }
}

export default Board;
