import React from 'react';
import * as Chess from 'chess.js';
import '../index.css';
class Board extends React.Component{



  constructor(props){
    super(props);
    this.chessBoard = new Chess();
    console.log("board is", this.chessBoard);

  }

  processClick(position){
    //TODO handle position click.
    console.log("Position is", position);
      console.log("board is in process", this.chessBoard);

  }

  render(){

    var color=0;
    var squares=[];
    for(let i=0;i<64;i++)
    {
      if(i%16===0){
        squares.push(<div className="square-white" key={i} onClick={() => {
          console.log(i);
          console.log("board is in process", this.chessBoard);
        }}></div>);
        color=0;
      }
      else if(i%8===0){
        squares.push(<div className="square-black" key={i} onClick={() => {
          console.log(i);
        }}></div>);
        color=1;
      }
      else if(color===1){
        squares.push(<div className="square-white" key={i} onClick={() => {
          console.log(i);
        }}></div>);
        color=0;
      }
      else{
        squares.push(<div className="square-black" key={i} onClick={() => {
          console.log(i);
        }}></div>);
        color=1;
      }
    }
    return (

      <div className="board">{squares}</div>

    );
  }
}

export default Board;
