import '../index.css';
class Square extends React.Component{
  constructor(props){
    super(props);

    this.color = props.color;
    this.position = props.position;
    this.piece = props.piece;

    //console.log("My props is ", props);
  }
/*  getPiece(p){

      this.setState({piece :ini(p)});
      //console.log(this.piece)
    } */
  //  piece=this.getPiece(this.position);

  render(){
    return (
      <button className={ this.color } key={this.position} piece={this.piece} onClick={() => {
        console.log(this.position ,this.piece);
      }}>
      <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Chess_blt45.svg" />
      </button>
    );
  }

}

export default Square;
