import React from 'react';
// import * as Chess from 'chess.js';
import ChessManager from '../services/chessManager';
import Square from './Square';
import Piece from './Piece';
import '../index.css';
class Board extends React.Component{



/* constructor(props){
    super(props);
    this.chessBoard = new Chess();
    console.log("board is", this.chessBoard);

  }
*/
  processClick(position){
    //TODO handle position click.
    console.log("Position is", position);
      console.log("board is in process", this.chessBoard.board());

      this.chessBoard.move(this.retPosition(position));
      console.log("board after move", this.chessBoard.board);

  }
  retPosition(intpos){
    let yCord = (8-(intpos / 8));
    let xCord = intpos % 8;

    let xAxis = ['a','b','c','d','e','f','g','h'];

    let retVal = xAxis[xCord]+yCord;
    console.log(retVal);
    return retVal;

  }

  render(){

    var color=0;
    var squares=[];
    for(let i=0;i<64;i++)
    {
      if(i%16===0){
        squares.push(<Square color={"square-white"} position={this.retPosition(i)} piece={"KING"} />);
        color=0;
      }
      else if(i%8===0){
        squares.push(<Square color={"square-black"} position={i} piece={""} />);
        color = 1;
      }
      else if(color===1){
        squares.push(<Square color={"square-white"} position={i} piece={""} />);
        color=0;
      }
      else{
        squares.push(<Square color={"square-black"} position={i} currentPiece={""} />);
        color=1;
      }
    }
    return (

      <div className="board">{squares}</div>

    );
  }
}

export default Board;
