    this.setState({ address :  this.chessManager.map() });
  }
  eventClear=(e)=>{
    e.preventDefault();
    this.chessManager.clear();
    this.setState({ address : this.chessManager.map()});        //this.chess.fen())});
  }
  render(){
    return (
      <div>
      <button
        onClick={this.eventReset}
      >Reset</button>
      <button
        onClick={this.eventClear}
      >Clear Board</button>
      <MappedBoard
        address={this.state.address}
        movefunc={this.movefunc}
      />
      </div>
    );
  }
}

export default Game;
