import React from 'react';
//import * as Chess from 'chess.js';
import * as ChessManager from '../servies/ChessManager.js';
//import pieceMap from '../PieceMapping';
import  MappedBoard from './MappedBoard';
import '../index.css';
class Game extends React.Component{
  constructor(props){
    super(props);
    this.chess= new ChessManager();
  }
  state = {
    address:[]                                        //Array which stores image address of Board to be mapped
  };
  retPosition(y,x){                                   //returns postion of the square on the board
    let yCord = y;
    let xCord = x % 8;
    let xAxis = ['a','b','c','d','e','f','g','h'];
    let retVal = xAxis[xCord]+yCord;
    return retVal;
  }
  isEven(num){
    return (num % 2 === 0);
  }

  //TODO: Move to chess manager
  //map(str) was here

  movefunc = (position,piece) => {
    if(piece!==''){
    this.source = position;
    }
    else{
      if(this.source!==''){
        this.chess.move({from:this.source , to:position});
        this.setState({address : this.map(this.chess.fen())});
        this.source='';
      }
    }
  }


  eventReset=(e)=>{
    e.preventDefault();
    this.chess.reset();
    var a = this.map(this.chess.fen());
    this.setState({ address : a });
  }
  eventClear=(e)=>{
    e.preventDefault();
    this.chess.clear();
    this.setState({ address : this.map(this.chess.fen())});
  }
  render(){
    return (
      <div>
      <button
        onClick={this.eventReset}
      >Reset</button>
      <button
        onClick={this.eventClear}
      >Clear Board</button>
      <MappedBoard
        address={this.state.address}
        movefunc={this.movefunc}
      />
      </div>
    );
  }
}

export default Game;
