import React from 'react';
import Board from './components/Board';

class Ini extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
  for(var i=8;i>=1;i--){
    for(var j=0;j<8;j++){
      const p=this.Board.retPosition(i,j);
      if(p==='a1'){
      this.setState({piece:'R'});
    }}}
    return(this.state.piece);
  }

