import React from 'react';
import Board from './components/Board';
import Bishop from './pieces/bishop';
export default function ini(){
  var completeBoard =[];
  for(var i=8;i>=1;i--){
    var rowSquare =[];
    for(var j=0;j<8;j++){

      let yCord = i;
      let xCord = j % 8;

      let xAxis = ['a','b','c','d','e','f','g','h'];

      let retVal = xAxis[xCord]+yCord;

      if(retVal==="a0"){
        return("https://upload.wikimedia.org/wikipedia/commons/4/45/Chess_plt45.svg");
      }
    }
    completeBoard.push(rowSquare);
  }
}
