import React from 'react';
import * as Chess from 'chess.js';

import ini from '../initialBoard';
import  MappedBoard from './mappedBoard';
import '../index.css';
class Board extends React.Component{

 constructor(props){
    super(props);
    this.chess= new Chess();
    console.log("board is", this.chess);
    console.log(this.chess.ascii());
  //  this.chess.move('e3');
  //  console.log(this.chess.ascii());
    console.log(this.chess.fen());
  //  console.log(this.chess.get('e3'));
  //  console.log(this.chess.ascii());
  }

  state = {
    address:[]
  };
  /*  processClick(position){
    //TODO handle position click.
    console.log("Position is", position);
      console.log("board is in process", this.chessBoard.board());

      this.chessBoard.move(this.retPosition(position));
      console.log("board after move", this.chessBoard.board);

  }  */
  retPosition(y,x){
    let yCord = y;
    let xCord = x % 8;
    let xAxis = ['a','b','c','d','e','f','g','h'];
    let retVal = xAxis[xCord]+yCord;
    return retVal;
  }
  isEven(num){
    return (num % 2 === 0);
  }
  mapy(str){
    const c=[];

    for(let i=0 , j=0 ;i<43 && j<64;i++ ){
      var char=ini(str[i]);
      if(char === 8)
      {
        for(let k=1;k<=Number(char);k++){
          c[j]='';
          j++;
      }
      }
      else if(char === '/'){
        continue;
      }
      else{
      c[j] = char;
      j++;
      }
    }
    //console.log("Mapy returns", c);
    return(c);
  }
  movefunc = (srcPos,des) => {
    des = "'"+des+"'";
    srcPos = "'"+srcPos+"'";
    console.log("source",srcPos,"destination", des );

    this.chess.move({from:srcPos , to:des});
    console.log(this.chess.ascii);
    this.setState({ address : this.mapy(this.chess.fen()) })
  }


  eventReset=(e)=>{
    e.preventDefault();
    this.chess.reset();
    var a = this.mapy(this.chess.fen());
    console.log("value recv",a);
    this.setState({ address : a });
    console.log(this.state.address);
  }
  eventClear=(e)=>{
    e.preventDefault();
    this.chess.clear();
    this.setState({ address : this.mapy(this.chess.fen())});
  }

  render(){
    return (
      <div>
      <button
        onClick={this.eventReset}
      >Reset</button>
      <button
        onClick={this.eventClear}
      >Clear Board</button>
      <MappedBoard
        address={this.state.address}
        movefunc={this.movefunc}
      />
      </div>
    );
  }
}

export default Board;
