import React from 'react';
import {ini} from '../initialBoard';
import '../index.css';
class Square extends React.Component{
  constructor(props){
    super(props);

    this.color = props.color;
    this.position = props.position;
    this.currentPiece = props.piece;
    //console.log("My props is ", props);
  }


  render(){
    return (
      <button className={ this.color } key={this.position}  onClick={() => {
        console.log(this.position ,this.currentPiece);
      }}>

      </button>
    );
  }

}

export default Square;
