import React from 'react';
//import {ini} from '../initialBoard';
import * as Chess from 'chess.js';
import '../index.css';
class Square extends React.Component{
  constructor(props){
    super(props);
    this.state={
      pos:props.position,
      src:''
    //  des:''
    }
    this.color = props.color;
    this.position = props.position;
    this.piece = props.piece;
    this.chess=new Chess();
    //console.log("My props is ", props);
  }
/*  getPiece(p){

      this.setState({piece :ini(p)});
      //console.log(this.piece)
    } */
  //  piece=this.getPiece(this.position);
  handleClick=(e)=>{
    e.preventDefault();
    this.setState({src:this.piece});
    this.setState({pos:this.position});
    //this.setState({des :this.position});
    this.des=this.pos;
    this.move(this.state.src,this.des);
    console.log(this.state.pos);
    console.log(this.piece);
    console.log(this.position);
  }
  move(src,des){
    if(src!==''){
      this.chess.move(des);
      console.log(this.chess.ascii());
    }
  }

  render(){
    return (
      <button
        className={ this.color }
        key={this.position}
        piece={this.piece}
        onClick={this.handleClick}
      >
      <img src={this.piece} />
      </button>
    );
  }

}

export default Square;
