import React from 'react';
import * as Chess from 'chess.js';

import ini from '../initialBoard';
import  MappedBoard from './mappedBoard';
import '../index.css';
class Board extends React.Component{

 constructor(props){
    super(props);
    this.chess= new Chess();
  }
  state = {
    address:[]
  };
  retPosition(y,x){
    let yCord = y;
    let xCord = x % 8;
    let xAxis = ['a','b','c','d','e','f','g','h'];
    let retVal = xAxis[xCord]+yCord;
    return retVal;
  }
  isEven(num){
    return (num % 2 === 0);
  }
  mapy(str){
    const c=[];
    for(let i=0 , j=0 ;i<64 && j<64;i++ ){
      var char=ini(str[i]);
      if(char === Number(char))
      {
        for(let k=1;k<=Number(char);k++){
          c[j]='';
          j++;
      }
      }
      else if(char === '/'){
        continue;
      }
      else{
      c[j] = char;
      j++;
      }
    }
    return(c);
  }
  movefunc = (pos,piece) => {
    if(piece!==''){
    this.source = pos;
    console.log("source position is",this.source);
    }
    else{
      if(this.source!==''){
        this.chess.move({from:this.source , to:pos});
        this.setState({address : this.mapy(this.chess.fen())});
        this.source='';
      }
    }
  }


  eventReset=(e)=>{
    e.preventDefault();
    this.chess.reset();
    var a = this.mapy(this.chess.fen());
    console.log("value recv",a);
    this.setState({ address : a });
    console.log(this.state.address);
  }
  eventClear=(e)=>{
    e.preventDefault();
    this.chess.clear();
    this.setState({ address : this.mapy(this.chess.fen())});
  }

  render(){
    return (
      <div>
      <button
        onClick={this.eventReset}
      >Reset</button>
      <button
        onClick={this.eventClear}
      >Clear Board</button>
      <MappedBoard
        address={this.state.address}
        movefunc={this.movefunc}
      />
      </div>
    );
  }
}

export default Board;
