 constructor(props){
    super(props);
    this.state = {
      piece:''
    };
    this.chess = new Chess();
    console.log("board is", this.chess);
    console.log(this.chess.ascii());
    this.chess.move('e3');
    console.log(this.chess.ascii());
    console.log(this.chess.fen());
    console.log(this.chess.get('e3'));
    this.chess.load('rnb1kbnr/pppp1ppp/8/4p3/5PPq/8/PPPPP2P/RNBQKBNR w KQkq - 1 3');
    console.log(this.chess.ascii());

  }



  // this.setState({ piece : initialBoard()});
  /*  processClick(position){
    //TODO handle position click.
    console.log("Position is", position);
      console.log("board is in process", this.chessBoard.board());

      this.chessBoard.move(this.retPosition(position));
      console.log("board after move", this.chessBoard.board);

  }  */
  retPosition(y,x){
    let yCord = y;
    let xCord = x % 8;

    let xAxis = ['a','b','c','d','e','f','g','h'];

    let retVal = xAxis[xCord]+yCord;
    //console.log(retVal);
    return retVal;

  }
  isEven(num){
    return (num % 2 === 0);
  }

/*  getPiece(pos){

    return(this.setState({piece :ini(pos)}));
    //return(this.piece);
  }
*/
  render(){
    var completeBoard =[];
    for(var i=8;i>=1;i--){
      var rowSquare =[];
      for(var j=0;j<8;j++){
        const squareColor = (this.isEven(i) && this.isEven(j)) || (!this.isEven(i) && !this.isEven(j))? "square-white" : "square-black";
        rowSquare.push(<Square
                          color={squareColor}
                          position={this.retPosition(i,j)}
                          key={this.retPosition(i,j)}
                          piece={this.state.piece}
                      />);
      }
      completeBoard.push(rowSquare);
    }

  /*  var color=0;
    var squares=[];
    for(let i=0;i<64;i++)
    {
      if(i%16===0){
        squares.push(<Square color={"square-white"} position={this.retPosition(i)} piece={"KING"} />);
        color=0;
      }
      else if(i%8===0){
        squares.push(<Square color={"square-black"} position={this.retPosition(i)} piece={""} />);
        color = 1;
      }
      else if(color===1){
        squares.push(<Square color={"square-white"} position={this.retPosition(i)} piece={""} />);
        color=0;
      }
      else{
        squares.push(<Square color={"square-black"} position={this.retPosition(i)} currentPiece={""} />);
        color=1;
      }
    }  */
    return (
      <div>
      <button onclick={(e) => this.setState({piece:'R'})} >Reset</button>
      <div className="board">{completeBoard}</div>

      </div>
    );
  }
}

export default Board;
